select to_int64(0, '0000000000000000000000000000000000000000000000ffffffffffffffffff'); -- -1 still
select to_int64(0, '00000000000000000000000000000000000000000000000ffffffffffffffff1'); -- -15
select to_int64(0, 'ff');

